function addShields( p, a ) -- ply, amount
	p.sAmount = p.sAmount + a
	p:SetNWInt( "shields", p.sAmount )
end

function removeShields( p, a ) -- ply, amount
	p.sAmount = p.sAmount - a
	p:SetNWInt( "shields", p.sAmount )
end

